delete.foreach:
DELETE FROM drug_user_cart
WHERE
  user_id = #{item.userId,jdbcType=BIGINT}
AND
  drug_id = #{item.drugId,jdbcType=INTEGER}

mapper.update:
UPDATE
  drug_user_cart
SET
  quantity = quantity + #{quantity}
WHERE
  user_id = #{userId,jdbcType=BIGINT}
AND
  drug_id = #{drugId,jdbcType=INTEGER}
