mapper.insert:
INSERT INTO drug_user_pharmacist(
  user_id,
  pharmacist_id,
  create_time
)
VALUES(
  #{userId},
  #{pharmacistId},
  #{createTime}
)

mapper.select:
SELECT COUNT(0) FROM drug_user_pharmacist
WHERE
  user_id = #{userId,jdbcType=BIGINT}
AND
  pharmacist_id = #{pharmacistId}

mapper.insert:
INSERT INTO drug_user_store(
  user_id,
  store_id,
  create_time
)
VALUES(
  #{userId},
  #{storeId},
  #{createTime}
)

mapper.select:
SELECT COUNT(0) FROM drug_user_store
WHERE
  user_id = #{userId, jdbcType=BIGINT}
AND
  store_id = #{storeId}
